// Loop over input items and transform the citation format
for (const item of $input.all()) {
  // Assuming chunks are available in item.json.chunks 
  // If chunks come from a different input, modify this line accordingly
  const chunks = $('Aggregate').first().json.data || [];

  if (item.json.output && Array.isArray(item.json.output)) {
    const transformedOutput = [];

    for (const outputItem of item.json.output) {
      if (outputItem.sentence_paragraph_with_citation) {
        const textWithCitations = outputItem.sentence_paragraph_with_citation;

        // Extract citation indices using regex to find [0], [1], etc.
        const citationMatches = textWithCitations.match(/\[(\d+)\]/g) || [];
        const citationIndices = citationMatches.map(match => parseInt(match.replace(/[\[\]]/g, '')));

        // Remove citations from text to get clean text
        const cleanText = textWithCitations.replace(/\s*\[\d+\]/g, '').trim();

        // Build citations array
        const citations = [];
        const processedIndices = new Set(); // To avoid duplicates

        for (const citationId of citationIndices) {
          if (!processedIndices.has(citationId)) {
            // Find chunk by chunk_id instead of using array index
            const chunkItem = chunks.find(chunkItem => chunkItem.chunk_id === citationId);

            if (chunkItem && chunkItem.document) {
              const chunk = chunkItem.document;
              const metadata = chunk.metadata;

              citations.push({
                chunk_index: citationId,
                chunk_source_id: metadata.source_id,
                chunk_lines_from: metadata.loc.lines.from,
                chunk_lines_to: metadata.loc.lines.to
              });
              processedIndices.add(citationId);
            }
          }
        }

        transformedOutput.push({
          text: cleanText,
          citations: citations
        });
      }
    }

    // Create the exact output format you specified
    item.json.output = {
      type: "ai",
      content: JSON.stringify({
        output: transformedOutput
      }),
      tool_calls: [],
      additional_kwargs: {},
      response_metadata: {},
      invalid_tool_calls: []
    };
  }
}

return $input.all();

